import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'register-post',
  templateUrl: './register-post.html',
  styleUrls: ['./register-post.css']
})

  export interface Categories {
  catList: Categorie[];
}

export interface Categorie {
  id: number;
  name: string;
}

export class RegisterPostComponent {



  public static UrlCategories: string = 'https://localhost:7080/Category/GetAllCategories';

  constructor(private http: HttpClient) {}

  onClickSubmit(data: { title: string, description: string}) {
    alert(data.title);
    alert(data.description);
  }

  returnAllCategories() {
    var returnVal;
    this.http.get(RegisterPostComponent.UrlCategories).subscribe((data) => {
      console.log(data);
    });
  }
}
