import { Component } from '@angular/core';
import { GlobalVariables, Categories, Posts } from 'src/app/app.globals';
import { HttpClient, HttpHeaders } from '@angular/common/http';


@Component({
  selector: 'home-panel',
  templateUrl: './home-panel.html',
  styleUrls: ['../../admin-panel-and-home-panel.css']
})

export class HomePanelComponent {
  public categories: Categories[] = []
  public posts: Posts[] = [];
  public static idPost: number = 0;

  constructor(private http: HttpClient) {
    http.get<Categories[]>(GlobalVariables.getAllCategoriesApiUrl).subscribe(data => { this.categories = data; }, error => console.log(error));
  }

  onClickSubmit(data: { categoryFK: any }) {
    this.http.get<Posts[]>(GlobalVariables.getAllPostsByCategorie + data.categoryFK).subscribe(res =>
    {
    this.posts = res; if (!this.posts[0]) { alert('There are no posts in this category') }
    },
    error => console.log(error));
  }

  sendIdToApi(event: { target: any; srcElement: any; currentTarget: any; }) {
    var target = event.target || event.srcElement || event.currentTarget;
    var idAttr = target.attributes.id;
    var value = idAttr.nodeValue;

    HomePanelComponent.idPost = value;
  }
}
<!DOCTYPE html>
<html>

<head>
  <meta charset="utf-8" />
  <title></title>
</head>

<body>

  <div class="internalGrid">
    <div class="left">
      <h3>Select</h3>
      <div class="categoryBox">

        <form #categoryId="ngForm" (ngSubmit)="onClickSubmit(categoryId.value)">
          <select name="categoryFK" ngModel>
            <option *ngFor="let categorie of categories" value="{{categorie.id}}"> {{ categorie.name }} </option>
          </select>
          <button type="submit">Search</button>
        </form>
      </div>
    </div>

    
  <div class="right">
    <div class="postList">
      <div class="postListSinglePost" *ngFor="let post of posts">
        <h2 class="clickable" (click)="sendIdToApi($event)" [routerLink]="['/read-post']" id="{{post.id}}">{{ post.title }}</h2>
        <p innerHtml="{{ post.description | slice: 0:500}}"></p>
        <div class="categorie-and-date">
          <p><b>Category:</b> {{ post.categorie }}</p>
          <p><b>Date release/update:</b> {{ post.date }}</p>
        </div>
      </div>
    </div>
  </div>
  
  </div>

</body>
</html>
