import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from "@angular/router";
import { AdminPanelComponent } from '../admin-panel/admin-panel.component';


@Component({
  selector: 'edit-post',
  templateUrl: './edit-post.html',
  styleUrls: ['./edit-post.css']
})

export class EditPostComponent {
  public categories: Categories[] = [];
  public posts: Posts[] = [];
  public static Url: string = 'https://localhost:7080/User/';
  public static UrlPost: string = 'https://localhost:7080/Post/GetById?id=6';




  constructor(private http: HttpClient, private router: Router) {

    http.get<Posts[]>(EditPostComponent.UrlPost).subscribe(response => { this.posts = response; }, error => console.log(error));
    http.get<Categories[]>(EditPostComponent.Url).subscribe(data => { this.categories = data; }, error => console.log(error));

  }

  onClickSubmit() {
    alert(AdminPanelComponent.idPost);
  }



}

interface Categories {
  id: number;
  name: string;
}

interface Posts{
  id: number;
  title: string;
  description: string;
  categoryFK: number;
}

<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8" />
  <title></title>
</head>
<body>
  <form #postId="ngForm" (ngSubmit)="onClickSubmit()">
    <h3>Post Title</h3>
    <input type="text" name="title" placeholder="Title" ngModel />
    <h3>Post Description</h3>
    <textarea name="description" ngModel></textarea>
    <h3>Post Category</h3>
    <br />
    <select name="categoryFK" ngModel>
      <option *ngFor="let categorie of categories" value="{{categorie.id}}"> {{ categorie.name }} </option>
    </select>
    <button type="submit">Submit</button>
  </form>


  <div class="postListSinglePost" *ngFor="let post of posts">
    <h2 (click)="sendIdToApi($event)" [routerLink]="['/edit-post']" id="{{post.id}}">{{ post.title }}</h2>
    <p>{{ post.description }}</p>
    <p>{{ post.categorie }}</p>
    <p>{{ post.date }}</p>
    <div class="postListSinglePostActions">
      <button>Update</button>
      <button>Delete</button>
    </div>
  </div>
</body>
</html>
