import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Component, Injectable } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from "@angular/router";
import { AdminPanelComponent } from '../admin-panel/admin-panel.component';
import { map, catchError, retry } from 'rxjs/operators';
import { Observable, throwError } from 'rxjs';


@Component({
  selector: 'edit-post',
  templateUrl: './edit-post.html',
  styleUrls: ['./edit-post.css']
})


export class EditPostComponent {

  post: Post | undefined;


  public categories: Categories[] = [];
  public static Url: string = 'https://localhost:7080/Category/GetAllCategories';
  public static UrlPost: string = 'https://localhost:7080/Post/GetById?id=';
  public static UrlCategories: string = 'https://localhost:7080/Category/GetAllCategories';


  constructor(private http: HttpClient, postId: number) {
    http.get<Categories[]>(EditPostComponent.UrlCategories).subscribe(data => { this.categories = data; }, error => console.log(error));
  }

  ngOnInit() {
    this.getPostById().subscribe((data: Post) => this.post = {
      id: data.id,
      title: data.title,
      description: data.description,
      date: data.date,
      categoryFK: data.categoryFK,
      category: data.category,
    });
  }

  getPostById() {
    return this.http.get<Post>(EditPostComponent.UrlPost);
  }



  onClickSubmit() {
    alert(AdminPanelComponent.idPost);
  }
}

export interface Post{
  id: number;
  title: string;
  description: string;
  date: string;
  categoryFK: number;
  category: any;
}

interface Categories {
  id: number;
  name: string;
}
