import { getLocaleDateTimeFormat } from '@angular/common';
import { HttpClient,HttpHeaders } from '@angular/common/http';

import { Component } from '@angular/core';

@Component({
  selector: 'register-post',
  templateUrl: './register-post.html',
  styleUrls: ['./register-post.css']
})



export class RegisterPostComponent {
  public categories: Categories[] = [];
  selectedOption: number = 0;


  public static UrlCategories: string = 'https://localhost:7080/Category/GetAllCategories';

  constructor(private http: HttpClient) {

    http.get<Categories[]>(RegisterPostComponent.UrlCategories).subscribe(data => { this.categories = data; }, error => console.log(error));

  }

  onClickSubmit(data: { title: string, description: string) {
    const now = new Date();
    let headers = new HttpHeaders();
    headers.append('Content-Type', 'application/json');
    var urlPostPost = "https://localhost:7080/Post";
    var postObj = {
      "title": data.title,
      "description": data.description,
      "date": now,
     // "selectedOption": data.selectedOption
    }

    alert(this.selectedOption);

    //this.http.post(urlPostPost, postObj, { headers: headers }).subscribe(res => { alert("Post " + data.title + " Registrada com sucesso!") });

  }
}

interface Categories {
  id: number;
  name: string;
}
<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8" />
    <title></title>
</head>
<body>
  <form #postId="ngForm" (ngSubmit)="onClickSubmit(postId.value)">
    <h3>Post Title</h3>
    <input type="text" name="title" placeholder="Title" ngModel/>
    <h3>Post Description</h3>
    <textarea name="description" ngModel></textarea>
    <h3>Post Category</h3>
    <br />
    <select [(ngModel)]="selectedOption">
      <option *ngFor="let categorie of categories" value="{{categorie.id}}" > {{ categorie.name }} </option>
    </select>
    <button type="submit">Submit</button>
  </form>

 
</body>
</html>
