import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';

@Component({
  selector: 'login-page',
  templateUrl: './login-page.html',
  styleUrls: ['./login-page.css']
})
export class LoginPageComponent {
  constructor(private http: HttpClient) {

  }

  listarUsuarioPorId() {
    this.http.get('https://localhost:7080/User/1').subscribe(resultado => console.log(resultado));
   

      
  }

  onClickSubmit(data: { id: number }) {
    let url: "https://localhost:7080/User";
    let userData = {
      Id: data.id,
    }
    let userJsonStr = JSON.stringify(userData);
    alert(userJsonStr);

   

   
  }

 
}
<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8" />
    <title></title>
</head>
<body>
  <div class="loginBox">
    <form #userlogin="ngForm" (ngSubmit)="onClickSubmit(userlogin.value)">
      <input type="text" name="login" placeholder="Login" ngModel>
      <input type="password" name="password" placeholder="Password" ngModel/>
      <br />
      <br />
      <input type="submit" value="submit">
    </form>

    <button (click)="listarUsuarioPorId()">GET /id</button>

  </div>
</body>
</html>
