import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'register-post',
  templateUrl: './register-post.html',
  styleUrls: ['./register-post.css']
})
export class RegisterPostComponent {

  onClickSubmit(data: { Title: string, Description: string, Category: number }) {
    alert(data.Title, data.Description, data.Category);
  }
}
<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8" />
    <title></title>
</head>
<body>
  <form (ngSubmit)="onClickSubmit()">
    <h3>Post Title</h3>
    <input type="text" name="Title" />
    <h3>Post Description</h3>
    <textarea name="Description"></textarea>
    <h3>Post Category</h3>
    <select></select>
    <br />
    <button type="submit">Submit</button>
  </form>
</body>
</html>
