import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from "@angular/router";


@Component({
  selector: 'admin-panel',
  templateUrl: './admin-panel.html',
  styleUrls: ['./admin-panel.css']
})


export class AdminPanelComponent implements OnInit {

  public posts: Posts[] = [];

  public static Url: string = 'https://localhost:7080/User/';
  public static UrlPosts: string = 'https://localhost:7080/Post/GetAllPostsWithCategorieAndDate';
 
  constructor(private http: HttpClient, private router: Router) {
    http.get<Posts[]>(AdminPanelComponent.UrlPosts).subscribe(data => { this.posts = data; }, error => console.log(error));
  }

  ngOnInit() {
    var session = sessionStorage.getItem('logged');
    if (session != 'true') {
      alert("You need to Log in to use this page!");
      this.router.navigate(['/login-page']);
    }
  }



  onClickSubmit(data: { category: string }) {
    let headers = new HttpHeaders();
    headers.append('Content-Type', 'application/json');
    var urlPostCategory = "https://localhost:7080/Category"
    var categoryObj = {
      "name": data.category,
    }


    this.http.post(urlPostCategory, categoryObj, { headers: headers }).subscribe(res => { alert("Categoria " + data.category + " Registrada com sucesso!") });
    
  }

  sendIdToApi(event: { target: any; srcElement: any; currentTarget: any; }) {
    var target = event.target || event.srcElement || event.currentTarget;
    var idAttr = target.attributes.id;
    var value = idAttr.nodeValue;

    alert(value);
  }
}

interface Posts {
  title: string;
  description: string;
  date: string;
  categorie: string;
  id: number;
}
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8" />
  <title></title>
</head>

<body>
  <div class="internalGrid">
    <div class="categoryBox">
      <form #categoryId="ngForm" (ngSubmit)="onClickSubmit(categoryId.value)">
        <input type="text" name="category" placeholder="Category" ngModel>
        <button type="submit">Register</button>
      </form>
    </div>


    <div class="postList">

      <div class="postListSinglePost" *ngFor="let post of posts" >
        <h2  (click)="sendIdToApi()" id="{{post.id}}" [routerLink]="['/edit-post']" >{{ post.title }}</h2>
        <p >{{ post.description }}</p>
        <p>{{ post.categorie }}</p>
        <p>{{ post.date }}</p>
        <div class="postListSinglePostActions">
          <button>Update</button>
          <button>Delete</button>
        </div>
      </div>

    </div>

    <a class="nav-link text-dark" [routerLink]="['/register-post']">New Post</a>

  </div>
</body>


</html>
