import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Component, Injectable } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from "@angular/router";
import { AdminPanelComponent } from '../admin-panel/admin-panel.component';
import { map, catchError, retry } from 'rxjs/operators';
import { Observable, throwError } from 'rxjs';


@Component({
  selector: 'edit-post',
  templateUrl: './edit-post.html',
  styleUrls: ['./edit-post.css']
})

export class EditPostComponent {

  post: Post | undefined;
  public categories: Categories[] = [];
  public static Url: string = 'https://localhost:7080/Category/GetAllCategories';
  public static UrlPost: string = 'https://localhost:7080/Post/GetById?id=';
  public static UrlCategories: string = 'https://localhost:7080/Category/GetAllCategories';
  public PostId: number = 0;


  constructor(private http: HttpClient) {
    http.get<Categories[]>(EditPostComponent.UrlCategories).subscribe(data => { this.categories = data; }, error => console.log(error));
    this.PostId = AdminPanelComponent.idPost;
  }

  ngOnInit() {
   
    this.getPostById().subscribe((data: Post) => this.post = {
      id: data.id,
      title: data.title,
      description: data.description,
      date: data.date,
      categoryFK: data.categoryFK,
      category: data.category,
    });
  }

  getPostById() {
    return this.http.get<Post>(EditPostComponent.UrlPost + this.PostId);
  }



  onClickSubmit(data: { title: string, description: string, categoryFK: number }) {
    const now = new Date();
    let headers = new HttpHeaders();
    headers.append('Content-Type', 'application/json');

    var urlPostPut = "https://localhost:7080/Post/" + this.PostId;
    var putObj = {
      "title": data.title,
      "description": data.description,
      "date": now.toLocaleDateString(),
      "categoryFK": data.categoryFK
    }

    console.log(data.description);

    //this.http.put(urlPostPut, putObj, { headers: headers }).subscribe(res => { alert("Post " + this.post?.title + " Editado com sucesso!") });
  }
}

export interface Post{
  id: number;
  title: string;
  description: string;
  date: string;
  categoryFK: number;
  category: any;
}

interface Categories {
  id: number;
  name: string;
}
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8" />
  <title></title>
</head>
<body>
  <form #postId="ngForm" (ngSubmit)="onClickSubmit(postId.value)">
    <h3>Title</h3>
    <input type="text" name="title" placeholder="Title" value="{{post?.title}}" ngModel />
    <h3>
      Description
    </h3>
    <textarea name="description" value="{{post?.description}}" ngModel></textarea>
    <h3>Post Category</h3>
    <select name="categoryFK" ngModel>
      <option *ngFor="let categorie of categories" value="{{categorie.id}}"> {{ categorie.name }} </option>
    </select>
    <br />

    <button type="submit">Submit</button>
  </form>

</body>
</html>
