import { getLocaleDateTimeFormat } from '@angular/common';
import { HttpClient,HttpHeaders } from '@angular/common/http';

import { Component } from '@angular/core';

@Component({
  selector: 'register-post',
  templateUrl: './register-post.html',
  styleUrls: ['./register-post.css']
})



export class RegisterPostComponent {
  public categories: Categories[] = [];


  public static UrlCategories: string = 'https://localhost:7080/Category/GetAllCategories';

  constructor(private http: HttpClient) {

    http.get<Categories[]>(RegisterPostComponent.UrlCategories).subscribe(data => { this.categories = data; }, error => console.log(error));

  }

  onClickSubmit(data: { title: string, description: string, categoryFK: number }) {
    const now = new Date();
    let headers = new HttpHeaders();
    headers.append('Content-Type', 'application/json');
    var urlPostPost = "https://localhost:7080/Post";
    var postObj = {
      "title": data.title,
      "description": data.description,
      "date": now,
      "categoryFK": data.categoryFK
    }

    alert(data.categoryFK);

    //this.http.post(urlPostPost, postObj, { headers: headers }).subscribe(res => { alert("Post " + data.title + " Registrada com sucesso!") });

  }
}

interface Categories {
  id: number;
  name: string;
}
