import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from "@angular/router";


@Component({
  selector: 'admin-panel',
  templateUrl: './admin-panel.html',
  styleUrls: ['./admin-panel.css']
})


export class AdminPanelComponent implements OnInit {

  public static Url: string = 'https://localhost:7080/User/';
 
  constructor(private http: HttpClient, private router: Router) {

  }

  ngOnInit() {
    var session = sessionStorage.getItem('logged');
    if (session != 'true') {
      alert("You need to Log in to use this page!");
      this.router.navigate(['/login-page']);
    }
  }



  onClickSubmit(data: { category: string }) {
    let headers = new HttpHeaders();
    headers.append('Content-Type', 'application/json');
    var urlPostCategory = "https://localhost:7080/Category"
    var categoryObj = {
      "name": data.category,
    }


    this.http.post(urlPostCategory, categoryObj, { headers: headers }).subscribe(res => { alert("Categoria " + data.category + " Registrada com sucesso!") });
    
  }


}
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8" />
  <title></title>
</head>

<body>



  <div class="categoryBox">
    <form #categoryId="ngForm" (ngSubmit)="onClickSubmit(categoryId.value)">
      <input type="text" name="category" placeholder="Category" ngModel>
      <button type="submit">Register</button>
    </form>
  </div>

</body>


</html>
