import { HttpClient, HttpClientModule } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'register-post',
  templateUrl: './register-post.html',
  styleUrls: ['./register-post.css']
})



export class RegisterPostComponent {
  public categories: Categories[] = [];


  public static UrlCategories: string = 'https://localhost:7080/Category/GetAllCategories';

  constructor(private http: HttpClient, private httpModule: HttpClientModule) { }

  onClickSubmit(data: { title: string, description: string}) {
    alert(data.title);
    alert(data.description);
  }

  returnAllCategories() {
   this.http.get<Categories[]>(RegisterPostComponent.UrlCategories).subscribe(data => { this.categories = data; }, error => console.log(error));

  }
}

interface Categories {
  id: number;
  name: string;
}
