import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { error } from 'console';

@Component({
  selector: 'login-page',
  templateUrl: './login-page.html',
  styleUrls: ['./login-page.css']
})
export class LoginPageComponent {

  public static Url: string = 'https://localhost:7080/User/';
  constructor(private http: HttpClient) {

  }



  onClickSubmit(data: { login: string, password: string }) {

    let loginUrl: string = LoginPageComponent.Url + data.login + '/' + data.password;
    this.http.get(loginUrl).subscribe(data => alert('Bem vindo! ' + data), (error => alert('User not found')));    
    
  } 
}
<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8" />
    <title></title>
</head>
<body>
  <div class="loginBox">
    <form #userlogin="ngForm" (ngSubmit)="onClickSubmit(userlogin.value)">
      <input type="text" name="login" placeholder="Login" ngModel>
      <input type="password" name="password" placeholder="Password" ngModel />
      <button type="submit">LOGIN</button>
    </form>



  </div>
</body>
</html>
