import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { Component, Injectable } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from "@angular/router";
import { AdminPanelComponent } from '../admin-panel/admin-panel.component';
import { map, catchError, retry } from 'rxjs/operators';
import { Observable, throwError } from 'rxjs';


@Component({
  selector: 'edit-post',
  templateUrl: './edit-post.html',
  styleUrls: ['./edit-post.css']
})

 @Injectable()
export class EditPostComponent {

  post: Post | undefined;



  public static Url: string = 'https://localhost:7080/Category/GetAllCategories';
  public static UrlPost: string = 'https://localhost:7080/Post/GetById?id=5';


  constructor(private http: HttpClient) {

  }

  ngOnInit() {
    this.getPostById().subscribe((data: Post) => this.post = {
      id: data.id,
      title: data.title,
      description: data.description,
      date: data.date,
      categoryFK: data.categoryFK,
      category: data.category,
    });
  }

  getPostById() {
    return this.http.get<Post>(EditPostComponent.UrlPost);
  }

  onClickSubmit() {
    this.getPostById().subscribe((data: Post) => this.post = {
      id: data.id,
      title: data.title,
      description: data.description,
      date: data.date,
      categoryFK: data.categoryFK,
      category: data.category,
    });

    alert(this.post?.title);
  }
}

export interface Post{
  id: number;
  title: string;
  description: string;
  date: string;
  categoryFK: number;
  category: any;
}

<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8" />
  <title></title>
</head>
<body>
  <form #postId="ngForm" (ngSubmit)="onClickSubmit()">
    <h3></h3>
    <input type="text" name="title" placeholder="Title" ngModel />
    <h3>{{post.description}}</h3>
    <textarea name="description" ngModel></textarea>
    <h3>Post Category</h3>
    <br />

    <button type="submit">Submit</button>
  </form>

</body>
</html>
