import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from "@angular/router";
import { AdminPanelComponent } from '../admin-panel/admin-panel.component';
import { map } from 'rxjs/operators';


@Component({
  selector: 'edit-post',
  templateUrl: './edit-post.html',
  styleUrls: ['./edit-post.css']
})

export class EditPostComponent {

   post: Post = {
    id: 0,
    title: '',
    description: '',
    date: '',
    categoryFK: 0,
    category: '',
  };

  public object_name = {
  text: "abc"
  };
  public static Url: string = 'https://localhost:7080/Category/GetAllCategories';
  public static UrlPost: string = 'https://localhost:7080/Post/GetById?id=5';




  constructor(private http: HttpClient, private router: Router) {


  }

  onClickSubmit() {
    this.http.get<Post>(EditPostComponent.UrlPost).subscribe((data: Post) => this.post = {
      id: data.id
    });
  }
 }
export interface Post{
  id: number;
  title: string;
  description: string;
  date: string;
  categoryFK: number;
  category: any;
}

<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8" />
  <title></title>
</head>
<body>
  <form #postId="ngForm" (ngSubmit)="onClickSubmit()">
    <h3></h3>
    <input type="text" name="title" placeholder="Title" ngModel />
    <h3>Post Description</h3>
    <textarea name="description" ngModel></textarea>
    <h3>Post Category</h3>
    <br />

    <button type="submit">Submit</button>
  </form>

</body>
</html>
