import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})

export class LoginService {
  constructor() { }

  checkIfUserIsLoggedIn() {
    var session = sessionStorage.getItem('logged');
    if (session != 'true') {
      alert("You need to Log in to use this page!");
      this.router.navigate(['/login-page']);
    }
  }
}
