import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Categorie, Categories } from './categories-interface';

@Component({
  selector: 'register-post',
  templateUrl: './register-post.html',
  styleUrls: ['./register-post.css']
})



export class RegisterPostComponent {



  public static UrlCategories: string = 'https://localhost:7080/Category/GetAllCategories';

  constructor(private http: HttpClient) {}

  onClickSubmit(data: { title: string, description: string}) {
    alert(data.title);
    alert(data.description);
  }

  returnAllCategories() {
    this.http.get<Categorie>(RegisterPostComponent.UrlCategories).subscribe((data) => {
      console.log(data.name);
    });
  }
}
<!DOCTYPE html>
<html>
<head>
    <meta charset="utf-8" />
    <title></title>
</head>
<body>
  <form #postId="ngForm" (ngSubmit)="onClickSubmit(postId.value)">
    <h3>Post Title</h3>
    <input type="text" name="title" placeholder="Title" ngModel/>
    <h3>Post Description</h3>
    <textarea name="description" ngModel></textarea>
    <h3>Post Category</h3>
    <select>
      <option>Abc</option>
      <option>Def</option>
    </select>
    <br />
    <button type="submit">Submit</button>
  </form>

  <button (click)="returnAllCategories()">LISTA</button>
</body>
</html>
